import React, { useState } from 'react';
import TaskList from './features/task-list';
import TaskForm from './features/task-form';
import logo from './logo.svg';
import './App.css';
let list = [
  {id: 1, title: 'Learn React'},
  {id: 2, title: 'learn Nodejs'},
  {id: 3, title: 'learn Nextjs'}
];

function App() {
  const [tasks, setTask] = useState(list);
  const [form, showTaskForm] = useState(false);

  const onShowTaskForm = () => {
    showTaskForm(prev => !prev);
  }

  const onAddTask = (title) => {
    // alert(title);
    let newTask = {
      id: +new Date(),
      title: title,
      completed: false,
      category: "wip"
    }

    setTask()
  }

  let buttonText = form ? '-' : '+';
  return (
    <div className="App">
      <div className="row">
        <TaskForm show = {form} onHide = {onShowTaskForm} onNewTask = {onAddTask}/>
      </div>
      <div className='row'>
        <h2 className='App-header'>
          TASK APP
        </h2>
        <button className='btn' onClick={onShowTaskForm}>{buttonText}</button>
      </div>
      <h2>Tasky</h2>
      <TaskList tasks = {tasks} />
    </div>
  );
}

export default App;
